import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
    const [image, setImage] = useState(null);
    const [uploading, setUploading] = useState(false);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!image) {
            alert('Please select an image');
            return;
        }

        setUploading(true);

        try {
            const bucketName = 'priya-user-images';
            const response = await axios.get(`https://xyy8glfwp4.execute-api.us-east-1.amazonaws.com/dev/uploadFile?file_name=${encodeURIComponent(image.name)}&file_type=${encodeURIComponent(image.type)}&bucket_name=${encodeURIComponent(bucketName)}`);
            const uploadUrl = response.data.upload_url;

            await axios.put(uploadUrl, image, {
                headers: {
                    'Content-Type': image.type,
                }
            });

            alert('Image uploaded successfully');
        } catch (error) {
            console.error('Error uploading image:', error);
            alert('Error uploading image. Please try again.');
        } finally {
            setUploading(false);
        }
    };

    return (
        <div>
            <h1>Welcome to the Image Gallery</h1>
            <div>
                <label htmlFor="imageUpload">Upload Image:</label>
                <input
                    type="file"
                    id="imageUpload"
                    accept="image/*"
                    onChange={handleImageChange}
                    required
                />
                <button type="submit" onClick={handleSubmit} disabled={uploading}>
                    {uploading ? 'Uploading...' : 'Upload'}
                </button>
            </div>
            <Link to="/images" style={{ fontWeight: 'bold', color: 'lightblue' }}>View Gallery</Link>
        </div>
    );
};

export default Home;
