import React, { useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [image, setImage] = useState(null);
    const [uploading, setUploading] = useState(false);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!image) {
            alert('Please select an image');
            return;
        }

        setUploading(true);

        try {
            const reader = new FileReader();
            reader.onloadend = async () => {
                const formData = {
                    body: reader.result.split(',')[1],
                };
                const config = {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                };

                const fileName = image.name;
                const apiUrl = 'https://m10cwnd5ih.execute-api.us-east-1.amazonaws.com/dev/uploadFile';
                const queryParams = `?filename=${encodeURIComponent(fileName)}`;
                const url = apiUrl + queryParams;

                await axios.post(url, formData, config);

                alert('Image uploaded successfully');

                const imageUrl = `https://priya-user-images.s3.amazonaws.com/${fileName}`;
                saveImageUrlToDynamoDB(imageUrl);
            };
            reader.readAsDataURL(image);
        } catch (error) {
            console.error('Error uploading image:', error);
            alert('Error uploading image. Please try again.');
        } finally {
            setUploading(false);
        }
    };

    const saveImageUrlToDynamoDB = async (imageUrl) => {
        try {
            const response = await axios.post('https://9pbt9ip0bl.execute-api.us-east-1.amazonaws.com/dev/saveURL', {
                imageUrl: imageUrl
            });
            console.log(response.data.message);
        } catch (error) {
            console.error('Error saving image URL to DynamoDB:', error);
            alert('Error saving image URL to DynamoDB. Please try again.');
        }
    };

    return (
        <div>
            <h1>Welcome to the Image Gallery</h1>
            <div>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="imageUpload">Upload Image:</label>
                    <input
                        type="file"
                        id="imageUpload"
                        accept="image/*"
                        onChange={handleImageChange}
                        required
                    />
                    <button type="submit" disabled={uploading}>
                        {uploading ? 'Uploading...' : 'Upload'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Home;
